import React from 'react';
import Switch from 'react-switch';
import { FaBars } from 'react-icons/fa';
import "../../styles/App.scss";
import ScrapeBtn from '../buttons/ButtonList.jsx';
// import reactLogo from './assets/logo.svg';

const Tips = ({
  collapsed,
  rtl,
  image,
  handleToggleSidebar,
  handleCollapsedChange,
  handleRtlChange,
  handleImageChange,
}) => {
  return (
    <main>
      <div className="btn-toggle" onClick={() => handleToggleSidebar(true)}>
        <FaBars />
      </div>
      <header>
        <h1 id="main-title">
          {/* <img width={80} src={reactLogo} alt="react logo" />  */}
          Tips & Advice
        </h1>
        <p>Tips & Advice Scraped from "blank" Webpage. Click "Scrape Tips" to request more tips and info to this page. These are some articles
          for the user to gain some knowledge on how to save money and become a successful individual.
        </p>
        </header>
              {/* Body */}
      <ScrapeBtn>
        Scrape Tips
      </ScrapeBtn>
        <div className="social-bagdes">
          <a
            href="https://github.com/azouaoui-med/react-pro-sidebar"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              alt="GitHub stars"
              src="https://img.shields.io/github/stars/azouaoui-med/react-pro-sidebar?style=social"
            />
          </a>
          <a
            href="https://github.com/azouaoui-med/react-pro-sidebar"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              alt="GitHub forks"
              src="https://img.shields.io/github/forks/azouaoui-med/react-pro-sidebar?style=social"
            />
          </a>
        </div>
      {/* </header> */}

      <br />
      <div className="block ">
        <Switch
          height={16}
          width={30}
          checkedIcon={false}
          uncheckedIcon={false}
          onChange={handleCollapsedChange}
          checked={collapsed}
          onColor="#219de9"
          offColor="#bbbbbb"
        />
        <span>COLLAPSED</span>
      </div>
      <div className="block">
        <Switch
          height={16}
          width={30}
          checkedIcon={false}
          uncheckedIcon={false}
          onChange={handleRtlChange}
          checked={rtl}
          onColor="#219de9"
          offColor="#bbbbbb"
        />
        <span>RTL</span>
      </div>
      <div className="block">
        <Switch
          height={16}
          width={30}
          checkedIcon={false}
          uncheckedIcon={false}
          onChange={handleImageChange}
          checked={image}
          onColor="#219de9"
          offColor="#bbbbbb"
        />
        <span>IMAGE</span>
      </div>

      <footer>
        <small>
          Â© 2020 made with by -{' '}
          <a target="_blank" rel="noopener noreferrer" href="https://github.com/skip1113">
            Skip Khamvongsa
          </a>
        </small>
        <br />
        <div className="social-bagdes">
          <a href="https://github.com/skip1113/budget-view" target="_blank" rel="noopener noreferrer">
            <img
              alt="GitHub followers"
              src="https://img.shields.io/github/followers/azouaoui-med?label=github&style=social"
            />
          </a>
          <a href="https://www.linkedin.com/in/skip-khamvongsa/" target="_blank" rel="noopener noreferrer">
            <img
              alt="Twitter Follow"
              src="https://img.shields.io/badge/linkedin-%230077B5.svg?&style=for-the-badge&logo=linkedin&logoColor=white"
            />
          </a>
        </div>
      </footer>
    </main>
  );
};

export default Tips;
